pragma solidity ^0.8.0;

contract VRFRequestIDBase {

  /**
   * @notice returns the seed which is actually input to the VRF coordinator
   *
   * @dev To prevent repetition of VRF output due to repetition of the
   * @dev user-supplied seed, that seed is combined in a hash with the
   * @dev user-specific nonce, and the address of the consuming contract. The
   * @dev risk of repetition is mostly mitigated by inclusion of a blockhash in
   * @dev the final seed, but the nonce does protect against repetition in
   * @dev requests which are included in a single block.
   *
   * @param _userSeed VRF seed input provided by user
   * @param _requester Address of the requesting contract
   * @param _nonce User-specific nonce at the time of the request
   */
  function makeVRFInputSeed(
    bytes32 _keyHash,
    uint256 _userSeed,
    address _requester,
    uint256 _nonce
  )
    internal
    pure
    returns (
      uint256
    )
  {
    return uint256(keccak256(abi.encode(_keyHash, _userSeed, _requester, _nonce)));
  }

  /**
   * @notice Returns the id for this request
   * @param _keyHash The serviceAgreement ID to be used for this request
   * @param _vRFInputSeed The seed to be passed directly to the VRF
   * @return The id for this request
   *
   * @dev Note that _vRFInputSeed is not the seed passed by the consuming
   * @dev contract, but the one generated by makeVRFInputSeed
   */
  function makeRequestId(
    bytes32 _keyHash,
    uint256 _vRFInputSeed
  )
    internal
    pure
    returns (
      bytes32
    )
  {
    return keccak256(abi.encodePacked(_keyHash, _vRFInputSeed));
  }
}

contracts/RandomCryptoMarc.sol.content:
// contracts/RandomCryptoMarc.sol
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@chainlink/contracts/src/v0.8/VRFConsumerBase.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "hardhat/console.sol";



contract RandomCryptoMarc is ERC721URIStorage, VRFConsumerBase, Ownable {
    using SafeMath for uint256;
    using Strings for string;
    using Counters for Counters.Counter;

    Counters.Counter private _tokenIds;

    address marketplaceAddress;

    bytes32 internal keyHash;
    uint256 internal fee;
    uint256 public randomResult;
    address public VRFCoordinator;
    // rinkeby: 0xb3dCcb4Cf7a26f6cf6B120Cf5A73875B7BBc655B
    address public LinkToken;
    // rinkeby: 0x01BE23585060835E02B77ef475b0Cc51aA1e0709a

    struct Attributes {
        uint256 background;
        uint256 face;
        uint256 mouth;
        uint256 eyes;
        uint256 hair;
        uint256 glasses;
        uint256 hat;
    }

    struct Competences {
        uint256 competence1;
        uint256 competence2;
        uint256 competence3;
    }

    struct CryptoMarc {
        uint256 id;
        string name;
    }

    CryptoMarc[] public cryptomarcs;

    mapping(uint256 => Attributes) public attributes;
    mapping(uint256 => Competences) public competences;


    mapping(bytes32 => string) requestCToName;
    mapping(bytes32 => address) requestToSender;
    mapping(bytes32 => uint256) requestToTokenId;

    /**
     * Constructor inherits VRFConsumerBase
     *
     * Network: Kovan
     * Chainlink VRF Coordinator address: 0xdD3782915140c8f3b190B5D67eAc6dc5760C46E9
     * LINK token address:                0xa36085F69e2889c224210F603D836748e7dC0088
     * Key Hash: 0x6c3699283bda56ad74f6b855546325b68d482e983852a7a82979cc4807b641f4
     */
    constructor(address _VRFCoordinator, address _LinkToken, bytes32 _keyhash, address marketplace)
        public
        VRFConsumerBase(_VRFCoordinator, _LinkToken)
        ERC721("CryptoMarc", "MARC")
    {   
        VRFCoordinator = _VRFCoordinator;
        LinkToken = _LinkToken;
        keyHash = _keyhash;
        fee = 0.1 * 10**18; // 0.1 LINK
        marketplaceAddress = marketplace;
    }

    function requestNewRandomCryptoMarc(
        string memory name
    ) public returns (bytes32) {
        require(
            LINK.balanceOf(address(this)) >= fee,
            "Not enough LINK - fill contract with faucet"
        );
        bytes32 requestId = requestRandomness(keyHash, fee);
        requestCToName[requestId] = name;
        requestToSender[requestId] = msg.sender;
        return requestId;
    }

    function expand(uint256 randomValue, uint256 n) public pure returns (uint256[] memory expandedValues) {
        expandedValues = new uint256[](n);
        for (uint256 i = 0; i < n; i++) {
            expandedValues[i] = uint256(keccak256(abi.encode(randomValue, i)));
        }
        return expandedValues;
    }

    event CryptoMarcCreated (
      uint indexed cmId,
      string name
    );

    function fulfillRandomness(bytes32 requestId, uint256 randomNumber)
        internal
        override
    {
        randomResult = randomNumber;

        uint256[] memory numbers = expand(randomNumber, 7);

        _tokenIds.increment();

        uint256 newId = _tokenIds.current();
        uint256 background = (numbers[0] % 12);
        uint256 face = (numbers[1] % 2);
        uint256 mouth = (numbers[2] % 2);
        uint256 eyes = (numbers[3] % 2);
        uint256 hair = (numbers[4] % 2);
        uint256 glasses = (numbers[5] % 2);
        uint256 hat = (numbers[6] % 2);

        attributes[newId] = Attributes( 
                background,
                face,
                mouth,
                eyes,
                hair,
                glasses,
                hat
                );

        competences[newId] = Competences(
            (numbers[0] % 69) + 1,
            (numbers[1] % 69) + 1,
            (numbers[2] % 69) + 1
        );


        cryptomarcs.push(
            CryptoMarc(
                newId,
                requestCToName[requestId]
            )
        );
        _safeMint(requestToSender[requestId], newId);
        setApprovalForAll(marketplaceAddress, true);
        emit CryptoMarcCreated(newId, requestCToName[requestId]);
    }

    function getNumberOfCryptoMarcs() public view returns (uint256) {
        return cryptomarcs.length; 
    }

    function getLastTokenId() public view returns (uint256) {
        return _tokenIds.current(); 
    }

}

